import React, { useState, useEffect, useRef } from 'react';
import QRious from 'qrious';

export default function MyComponent() {
  const [qrcodeString, setQrcodeString] = useState(null);
  const phoneRef = useRef(null);
  const txtRef = useRef(null);
  const imgRef = useRef(null);

  const handleCreateQR = () => {
    if (!phoneRef.current) return;
    const qr = new QRious({
      element: imgRef.current,
      value: `https://api.whatsapp.com/send?phone=${phoneRef.current.value}&text=${txtRef.current.value}`,
      size: 200,
      level: 'H',
      background:"rgba(0, 0, 0, 0)"
    });
    setQrcodeString(qr.toDataURL());
  };

  const handleSave = () => {
    // check if QR code has been generated
    if (qrcodeString) {
      // create a link element
      const link = document.createElement("a");
      // set link's href to QR code image data
      link.href = qrcodeString;
      // set the file name
      link.download = "qrcode.png";
      // trigger the download
      link.click();
    } else {
      alert("QR code has not been generated yet!");
    }
  }

  return (
    <div style={{ textAlign: "center" }}>
      <input
        type="tel"
        pattern="[0-9]{3}[0-9]{3}[0-9]{4}"
        ref={phoneRef}
        placeholder="Enter phone number"
        style={{
          width: "50%",
          padding: "12px 20px",
          margin: "8px 0",
          boxSizing: "border-box",
          border: "2px solid #ccc",
          borderRadius: "4px",
          fontSize: "16px"
        }}
      />
      <input
        type="text"
        ref={txtRef}
        placeholder="Enter Text"
        style={{
          width: "50%",
          padding: "12px 20px",
          margin: "8px 0",
          boxSizing: "border-box",
          border: "2px solid #ccc",
          borderRadius: "4px",
          fontSize: "16px"
        }}
      />
      <br />
      <button
        onClick={handleCreateQR}
        style={{
          width: "20%",
          padding: "12px 20px",
          margin: "8px 0",
          boxSizing: "border-box",
          border: "2px solid #ccc",
          borderRadius: "4px",
          fontSize: "16px",
          backgroundColor: "#4CAF50",
          color: "white"
        }}
      >
        Create QR
      </button>
      {qrcodeString ? (
           <>
           <button
             onClick={handleSave}
             style={{
               width: "20%",
               padding: "12px 20px",
               margin: "8px 0",
               boxSizing: "border-box",
               border: "2px solid #ccc",
               borderRadius: "4px",
               fontSize: "16px",
               backgroundColor: "#4CAF50",
               color: "white"
             }}
           >
             Save
           </button>
           <br />
           <img
             ref={imgRef}
             src={qrcodeString}
             alt="QR code"
             style={{
               width: "30%",
               padding: "12px 20px",
               margin: "8px 0",
               boxSizing: "border-box",
               border: "2px solid #ccc",
               borderRadius: "4px",
               fontSize: "16px"
             }}
           />
         </>
       ) : null}
     </div>
   );
 }
