import React, { useState,  useRef } from "react";
import qrcode from "qrcode-generator";

function MyComponent() {
  const refLink = useRef(null);
  const [qrcodeString, setQrcodeString] = useState("");

  const handleCreateQR = () => {
    if (refLink.current) {
      const data = refLink.current.value;
      const typeNumber = 4;
      const errorCorrectionLevel = "L";
      const qr = qrcode(typeNumber, errorCorrectionLevel);
      qr.addData(data);
      qr.make();

      const canvas = document.createElement("canvas");
      canvas.width = qr.getModuleCount() * 5;
      canvas.height = qr.getModuleCount() * 5;
      const ctx = canvas.getContext("2d");
      for (let row = 0; row < qr.getModuleCount(); row++) {
        for (let col = 0; col < qr.getModuleCount(); col++) {
          ctx.fillStyle = qr.isDark(row, col) ? "#000" :  "rgba(0, 0, 0, 0)";
          ctx.fillRect(col * 5, row * 5, 5, 5);
        }
      }
      setQrcodeString(canvas.toDataURL());
    }
  };

  const handleSave = () => {
    // check if QR code has been generated
    if (qrcodeString) {
      // create a link element
      const link = document.createElement("a");
      // set link's href to QR code image data
      link.href = qrcodeString;
      // set the file name
      link.download = "qrcode.png";
      // trigger the download
      link.click();
    } else {
      alert("QR code has not been generated yet!");
    }
  };

  return (
    <div style={{ textAlign: "center" }}>
      <input
        type="text"
        ref={refLink}
        placeholder="Enter web page"
        style={{
          width: "50%",
          padding: "12px 20px",
          margin: "8px 0",
          boxSizing: "border-box",
          border: "2px solid #ccc",
          borderRadius: "4px",
          fontSize: "16px",
        }}
      />
      <br />
      <button
        onClick={handleCreateQR}
        style={{
          width: "20%",
          padding: "12px 20px",
          margin: "8px 0",
          boxSizing: "border-box",
          border: "2px solid #ccc",
          borderRadius: "4px",
          fontSize: "16px",
          backgroundColor: "#4CAF50",
          color: "white",
        }}
      >
        Create QR
      </button>
      {qrcodeString ? (
        <>
          <button
            onClick={handleSave}
            style={{
              width: "20%",
              padding: "12px 20px",
              margin: "8px 0",
              boxSizing: "border-box",
              border: "2px solid #ccc",
              borderRadius: "4px",
              fontSize: "16px",
              backgroundColor: "#4CAF50",
              color: "white",
            }}
          >
            Save
          </button>
          <br />
          <img
            src={qrcodeString}
            alt="QR code"
            style={{
              width: "30%",
              padding: "12px 20px",
              margin: "8px 0",
              boxSizing: "border-box",
              border: "2px solid #ccc",
              borderRadius: "4px",
              fontSize: "16px",
            }}
          />
        </>
      ) : null}
    </div>
  );
}
export default MyComponent;
